// Generated by Selenium IDE
const { Builder, By, Key, until } = require('selenium-webdriver')
const assert = require('assert')

describe('Test división', function() {
  this.timeout(30000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('chrome').build()
    vars = {}
  })
  afterEach(async function() {
    await driver.quit();
  })
  it('Test división', async function() {
    await driver.get("http://127.0.0.1:5500/fundamentos/calculadora/calculadora.html")
    await driver.manage().window().setRect({ width: 974, height: 778 })
    await driver.findElement(By.css("tr:nth-child(4) > td:nth-child(2) > .num")).click()
    await driver.findElement(By.css("tr:nth-child(3) .operation")).click()
    await driver.findElement(By.css("tr:nth-child(6) .num")).click()
    await driver.findElement(By.id("igual")).click()
    assert(await driver.findElement(By.id("acumulado")).getText() == "5/0=")
    assert(await driver.findElement(By.id("resultado")).getText() == "Infinity")
    await driver.findElement(By.id("limpiar")).click()
    await driver.findElement(By.css("tr:nth-child(4) > td:nth-child(2) > .num")).click()
    await driver.findElement(By.css("tr:nth-child(3) .operation")).click()
    await driver.findElement(By.css("tr:nth-child(3) > td:nth-child(2) > .num")).click()
    await driver.findElement(By.id("igual")).click()
    assert(await driver.findElement(By.id("acumulado")).getText() == "5/2=")
    assert(await driver.findElement(By.id("resultado")).getText() == "2,5")
  })
})
